// 1. Verificar que hay dos productos
cy.get('.cart_item').should('have.length', 2);
// 2. Remover el primer producto (Sauce Labs Backpack)
cy.get('.cart_item').first().find('.btn_secondary').click();
// 3. Verificar que solo queda un producto en el carrito
cy.get('.cart_item').should('have.length', 1);
// 4. Iniciar el checkout
cy.get('#checkout').click();
// 5. Llenar la información del formulario
cy.get('#first-name').type('Elisa');
cy.get('#last-name').type('Luna');
cy.get('#postal-code').type('1753');
// 6. Continuar
cy.get('#continue').click();
// 7. Verificar la página de resumen (Overview)
cy.url().should('include', '/checkout-step-two.html');
cy.get('.summary_total_label').should('contain', 'Total:');
// 8. Finalizar la compra
cy.get('#finish').click();
// 9. Verificar el mensaje de éxito
cy.url().should('include', '/checkout-complete.html');
cy.get('.complete-header').should('have.text', 'Thank you for your order!');

const itemName = 'Sauce Labs Fleece Jacket';
// 1. Encontrar y añadir el producto por su nombre
cy.contains('.inventory_item_name', itemName).parents('.inventory_item').find('button.btn_primary').click();
// 2. Verificar que el contador del carrito se actualizó a 1
cy.get('.shopping_cart_badge').should('have.text', '1');
// 3. Ir al carrito
cy.get('.shopping_cart_link').click();
// 4. Verificar que el producto está en la página del carrito
cy.url().should('include', '/cart.html');
cy.get('.inventory_item_name').should('have.text', itemName);

// 1. Ingresar credenciales y loguearse
cy.get('#user-name').type('standard_user');
cy.get('#password').type('secret_sauce');
cy.get('#login-button').click();
// 2. Verificar que el logueo fue exitoso
cy.url().should('include', '/inventory.html');
cy.get('.title').should('have.text', 'Products');
